sys_script.script:
if (current.operation() != 'insert' && current.comments.changes()) { 	 
	if (!isConnect()) 
		gs.eventQueue("x_1552560_training_incident_new.commented", current, gs.getUserID(), gs.getUserName()); 
} 

if (current.operation() == 'insert') {
 gs.eventQueue("x_1552560_training_incident_new.inserted", current, gs.getUserID(), gs.getUserName());
}

if (current.operation() == 'update'&& !current.comments.changes()) {
 gs.eventQueue("x_1552560_training_incident_new.updated", current, gs.getUserID(), gs.getUserName());
}

if (!current.assigned_to.nil() && current.assigned_to.changes()) {
  gs.eventQueue("x_1552560_training_incident_new.assigned", current, current.assigned_to.getDisplayValue() , previous.assigned_to.getDisplayValue());
}

if (!current.assignment_group.nil() && current.assignment_group.changes()) {
  gs.eventQueue("x_1552560_training_incident_new.assigned.to.group", current, current.assignment_group.getDisplayValue() , previous.assignment_group.getDisplayValue());
}


if (current.priority.changes() && current.priority == 1) {
  gs.eventQueue("x_1552560_training_incident_new.priority.1", current, current.priority, previous.priority);
}

if (current.severity.changes() && current.severity== 1) {
  gs.eventQueue("x_1552560_training_incident_new.severity.1", current, current.severity, previous.severity);
}

if (current.escalation.changes() && current.escalation > previous.escalation && previous.escalation != -1) {
  gs.eventQueue("x_1552560_training_incident_new.escalated", current, current.escalation , previous.escalation );
}

if (current.active.changesTo(false)) {
  gs.eventQueue("x_1552560_training_incident_new.inactive", current, current.x_1552560_training_incident_new_state, previous.x_1552560_training_incident_new_state);
  gs.workflowFlush(current);
}

function isConnect() { 
	var transaction = GlideTransaction.get(); 
	if (!transaction) 
		return false; 

	var request = transaction.getRequest();
	if (!request)
		return false; 

	var path = request.getRequestURI();
	return path && path.match(/\/api\/now\/connect/); 
}
